mapper.select:
select id,share_code, open_value, end_value,
rise_fall, rise_fall_ratio, highest,
lowest, date,total_amount,pre_end_value from market_info t
where 1=1

    and t.date >= #{startTime}


    and t.date  <= 
    #{endTime}


    and t.share_code = #{shareCode,jdbcType=VARCHAR}

order by t.date

select a.share_code as shareCode, a.end_value as endValue, a.total_amount as totalAmount, a.rise_fall_ratio as riseFallRatio, a.pre_end_value as preEndValue, a.date, c.share_name as shareName from (SELECT t.* FROM market_info t, ( SELECT MAX(date) AS date, share_code FROM market_info t
and t.date <= #{endTime}
and t.share_code = #{shareCode,jdbcType=VARCHAR}
GROUP BY t.share_code ) t2 WHERE t.share_code = t2.share_code AND t.date = t2.date UNION SELECT t3.* FROM market_info t3, ( SELECT min(t2.date) AS date, t2.share_code FROM market_info t2
and t2.date >=#{startTime}
and t2.share_code = #{shareCode,jdbcType=VARCHAR}
GROUP BY t2.share_code ) t4 WHERE t3.date = t4.date AND t3.share_code = t4.share_code) a LEFT JOIN share_info c ON c.share_code = a.share_code

SELECT DISTINCT share_code as shareCode, ROUND((maxHighest - lastEndValue) / lastEndValue * 100,2) AS maxRatio, date as startTime, last_value(sumRatio) over (PARTITION BY share_code order by date) as finalRatio, ROUND((minLowest - lastEndValue) / lastEndValue * 100,2) as minRatio FROM ( SELECT DISTINCT share_code, MAX(highest) over (PARTITION BY share_code) AS maxHighest, round(first_value ( end_value / (1 + rise_fall_ratio * 0.01)) over ( PARTITION BY share_code ORDER BY date), 2 ) AS lastEndValue, round(first_value (deal_amount) over (PARTITION BY share_code ORDER BY date),2) AS FIRSTDealAmount, round(first_value (turn_over_rate) over ( PARTITION BY share_code ORDER BY date), 2 ) AS FIRSTTurnOverRate, min(date) over( PARTITION BY share_code ORDER BY date) as date, SUM(rise_fall_ratio) over (PARTITION BY share_code ORDER BY date) as sumRatio, MIN(lowest) over (PARTITION BY share_code) as minLowest FROM ( SELECT share_code, rise_fall_ratio, highest, lowest, open_value, end_value, deal_amount, turn_over_rate, date FROM market_info_new WHERE share_code IN ( SELECT share_code FROM market_info_new WHERE share_code IN ( SELECT share_code FROM market_info_new WHERE date >= #{upStartTime} AND date < #{upEndTime} AND rise_fall_ratio > 0 GROUP BY share_code HAVING COUNT(share_code) >= #{minUpDay} AND SUM(rise_fall_ratio) > #{minRate} ) AND date >= #{downStartTime} AND date < #{downEndTime} AND rise_fall_ratio < 0 GROUP BY share_code HAVING COUNT(share_code) > #{minDownDay} ) AND date >= #{selectStartTime}
AND date < #{selectEndTime}
AND turn_over_rate > #{minTurnOverRate}
) AS hihi ) kk WHERE FIRSTDealAmount / FIRSTTurnOverRate > 80000000 ORDER BY (maxHighest - lastEndValue) / lastEndValue DESC
