GROUP BY share_code
HAVING COUNT( CASE WHEN rise_fall_ratio < 0  THEN 1 END ) > #{condition.minDownDay}
and  SUM(rise_fall_ratio)<#{condition.minDownRate}

select share_code as shareCode, ROUND((maxHighest - lastEndValue)/lastEndValue*100,3) as maxRatio, ROUND((minLowest - lastEndValue)/lastEndValue*100,3) as minRatio, ROUND((openValue - lastEndValue)/lastEndValue*100,3) as openRatio, sumRatio as finalRatio, minDate as startTime, deal_amount as dealAmount from ( select share_code, MAX(highest) over (PARTITION BY share_code) AS maxHighest, round( first_value ( end_value / (1 + rise_fall_ratio * 0.01) ) over ( PARTITION BY share_code ORDER BY date ), 3 ) AS lastEndValue, MIN(date) over (PARTITION BY share_code) AS minDate, date, SUM(rise_fall_ratio) over ( PARTITION BY share_code ORDER BY date ) AS sumRatio, MIN(lowest) over (PARTITION BY share_code) AS minLowest, first_value(open_value) over(partition by share_code order by date) as openValue, row_number()over(PARTITION BY share_code order by date) as rowKey, min(deal_amount)over(PARTITION BY share_code) as deal_amount from market_info_new where share_code in
#{it}
AND date >= #{condition.selectStartTime}
AND date <= #{condition.selectEndTime}
) final where rowKey = #{condition.recentRateDay}

SELECT share_code as shareCode, sumCount as rate FROM ( -- 查询满足条件的购买日期开始日期到规定购买的结束日期数据 开始 SELECT mfn.id, mfn.share_code, rise_fall_ratio, mfn.date, sn.date as firstDate, COUNT( CASE WHEN rise_fall_ratio > 0 THEN 1 END ) OVER ( PARTITION BY share_code,sn.date ORDER BY mfn.date ROWS BETWEEN 2 PRECEDING AND CURRENT ROW ) AS countUp, sum(rise_fall_ratio) over ( PARTITION BY share_code,sn.date ORDER BY mfn.date ) AS sumCount, row_number () over ( PARTITION BY share_code,sn.date ORDER BY sn.date ) AS rowhi FROM market_info_new mfn, ( -- 根据传入的code 和date 作为一个查询结果集 开始 SELECT share_code, date FROM market_info_new tt WHERE (share_code, date) IN (
( #{item.shareCode}, #{item.startTime} )
) -- 根据传入的code 和date 作为一个查询结果集 结束 ) sn WHERE mfn.share_code = sn.share_code AND mfn.date >= sn.date AND mfn.date <= ( SELECT date FROM trade_date WHERE id = (SELECT id FROM trade_date WHERE date = sn.date ) + 5- 1 ) -- 查询满足条件的购买日期开始日期到规定购买的结束日期数据 结束 ) kk WHERE -- 条件 取连红三天的记录 或者 或者是没有三连红到最后购买截止期限的 kk.rowhi = 5
