mapper.insert:
insert into market_info_new (id, share_code, open_value, 
  end_value, rise_fall, rise_fall_ratio, 
  highest, lowest, deal_count, 
  deal_amount, turn_over_rate, date
  )
values (#{id,jdbcType=INTEGER}, #{shareCode,jdbcType=VARCHAR}, #{openValue,jdbcType=DOUBLE}, 
  #{endValue,jdbcType=DOUBLE}, #{riseFall,jdbcType=DOUBLE}, #{riseFallRatio,jdbcType=DOUBLE}, 
  #{highest,jdbcType=DOUBLE}, #{lowest,jdbcType=DOUBLE}, #{dealCount,jdbcType=INTEGER}, 
  #{dealAmount,jdbcType=DOUBLE}, #{turnOverRate,jdbcType=DOUBLE}, #{date,jdbcType=TIMESTAMP}
  )

mapper.select:
select round(t1.netRadio,4) as netRatio,t2.share_name as shareName from (SELECT EXP( SUM( LN(( 1+rise_fall_ratio * 0.01 ))))-1 AS netRadio,share_code as shareCode FROM market_info_new WHERE date >= #{domain.startTimeDa} and date <=#{domain.endTimeDa} and SUBSTR(share_code,4,6) in
#{shareCode}
GROUP BY share_code) t1, share_info t2 where SUBSTR(t1.shareCode,4,6) = t2.share_code order by t1.netRadio desc

SELECT mark.*,sif.share_name FROM market_info_new mark,share_info sif WHERE date >= #{startTimeDa} and date <=#{endTimeDa} and SUBSTR(mark.share_code,4,6) in
#{shareCode}
and SUBSTR(mark.share_code,4,6) = sif.share_code and sif.status = 1
